import React, {useState} from 'react'
import './App.css';

function App() {

  const [str, setStr] = useState('')

  function setStateClick(set, message){
    set(message)
  }

  return (
    <div className='allPage'>
      <header className='header'>
        <p>
          {str}
        </p>
        <button onClick={() => setStateClick(setStr, text)}>CLIQUE AQUI</button>
      </header>
    </div>
  );
}

export default App;
