import React, {useState} from 'react'
import './App.css';

function App() {


  function setStateClick(set, message){
    const [str, setStr] = useState(initial)
    set(message)
  }

  return (
    <div className='allPage'>
      <header className='header'>
        <p>
          {str}
        </p>
        <button onClick={() => setStateClick(setStr, 'text')}>CLIQUE AQUI</button>
      </header>
    </div>
  );
}

export default App;
